function countVowels(s) {
  const vowels = new Set(['a','e','i','o','u']);
  let count = 0;
  for (const ch of s.toLowerCase()) if (vowels.has(ch)) count++;
  return count;
}
// Example: countVowels("hello") -> 2

function findMax(arr) {
  if (arr.length === 0) return null;
  let max = arr[0];
  for (let v of arr) if (v > max) max = v;
  return max;
}
// Example: findMax([3,1,6,2]) -> 6

function reverseString(s) {
  let res = '';
  for (let i = s.length - 1; i >= 0; i--) res += s[i];
  return res;
}
// Example: reverseString("abc") -> "cba"

function areAnagrams(a, b) {
  if (a.length !== b.length) return false;
  const map = {};
  for (const ch of a.toLowerCase()) map[ch] = (map[ch] || 0) + 1;
  for (const ch of b.toLowerCase()) {
    if (!map[ch]) return false;
    map[ch]--;
  }
  return true;
}
// Example: areAnagrams("listen","silent") -> true

function removeDuplicates(arr) {
  const seen = new Set();
  const res = [];
  for (const v of arr) {
    if (!seen.has(v)) { seen.add(v); res.push(v); }
  }
  return res;
}
// Example: removeDuplicates([1,2,2,3]) -> [1,2,3]

function firstNonRepeatingChar(s) {
  const freq = {};
  for (const ch of s) freq[ch] = (freq[ch] || 0) + 1;
  for (const ch of s) if (freq[ch] === 1) return ch;
  return '_';
}
// Example: firstNonRepeatingChar("swiss") -> "w"

function sumArray(arr) {
  let s = 0;
  for (const v of arr) s += v;
  return s;
}
// Example: sumArray([1,2,3]) -> 6

function isPalindrome(s) {
  s = s.toLowerCase();
  let l = 0, r = s.length - 1;
  while (l < r) {
    if (s[l] !== s[r]) return false;
    l++; r--;
  }
  return true;
}
// Example: isPalindrome("Racecar") -> true

function countWords(s) {
  return s.trim().split(/\s+/).filter(Boolean).length;
}
// Example: countWords("  hello  world ") -> 2

function mergeSorted(a, b) {
  const res = [];
  let i = 0, j = 0;
  while (i < a.length && j < b.length) {
    if (a[i] <= b[j]) res.push(a[i++]);
    else res.push(b[j++]);
  }
  while (i < a.length) res.push(a[i++]);
  while (j < b.length) res.push(b[j++]);
  return res;
}
// Example: mergeSorted([1,3],[2,4]) -> [1,2,3,4]

function indexOf(arr, target) {
  for (let i = 0; i < arr.length; i++) if (arr[i] === target) return i;
  return -1;
}

function isPrime(n) {
  if (n <= 1) return false;
  if (n <= 3) return true;
  if (n % 2 === 0) return false;
  for (let i = 3; i * i <= n; i += 2) if (n % i === 0) return false;
  return true;
}

function factorial(n) {
  let res = 1;
  for (let i = 2; i <= n; i++) res *= i;
  return res;
}

function fib(n) {
  if (n < 2) return n;
  let a = 0, b = 1;
  for (let i = 2; i <= n; i++) {
    [a, b] = [b, a + b];
  }
  return b;
}

function swap(a,b) {
  a = a ^ b;
  b = a ^ b;
  a = a ^ b;
  return [a,b];
}

function sumDigits(n) {
  let s = 0;
  while (n > 0) {
    s += n % 10;
    n = Math.floor(n / 10);
  }
  return s;
}

function capitalizeWords(s) {
  return s.split(' ').map(w => w ? w[0].toUpperCase() + w.slice(1) : w).join(' ');
}

function rotateRight(arr, k) {
  k = k % arr.length;
  return arr.slice(-k).concat(arr.slice(0, arr.length - k));
}
